// Timeslot.js
import React, { useState } from 'react';
import './Timeslot.css';

const activities = [
  "Projection Room Check",
  "Sound System Check",
  "Clean Projection Room",
  "Clean Sound Booth",
  "Check Projector Lamp",
  "Check 3D Glasses",
  "Check 4DX System",
  "Check Screen",
  "Check Sound Levels",
  "Check Emergency Exits",
  "Check Fire Extinguishers",
  "Check First Aid Kit",
  "Check Generator",
  "Check Air Conditioning",
  "Check Lighting"
];

const Timeslot = ({ timeslot, onBackClick }) => {
  const [selectedActivities, setSelectedActivities] = useState([]);

  const markActivity = async (activity) => {
    try {
      const response = await fetch('http://localhost:5001/api/mark-attendance', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ activity, timeslot }),
      });
      const data = await response.json();
      if (data.success) {
        setSelectedActivities(prev => [...prev, activity]);
      }
    } catch (error) {
      console.error('Error marking activity:', error);
    }
  };

  return (
    <div className="timeslot-container">
      <div className="header">
        <h1>Reel Technical Operations</h1>
        <h2 className="timeslot-title">Time Slot: {timeslot}</h2>
      </div>
      <div className="timeslot-buttons">
        {activities.map((activity, index) => (
          <button
            key={index}
            onClick={() => markActivity(activity)}
            className={`activity-btn ${selectedActivities.includes(activity) ? 'selected' : ''}`}
          >
            {activity}
          </button>
        ))}
      </div>
      <div className="action-buttons">
        <button onClick={onBackClick} className="back-btn">
          Back to Homepage
        </button>
        <a href="http://localhost:5001/api/download" className="download-btn">
          Download Sheet
        </a>
      </div>
    </div>
  );
};

export default Timeslot;
