import React, { useState } from 'react';
import './Timeslot.css';

const Timeslot = ({ timeslot, onBackClick }) => {
  const markAttendance = async (employee) => {
    try {
      const response = await fetch('http://localhost:5001/api/mark-attendance', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ employee, timeslot }),
      });
      const data = await response.json();
      if (data.success) {
        alert('Attendance marked successfully!');
      }
    } catch (error) {
      console.error('Error marking attendance:', error);
    }
  };

  return (
    <div className="timeslot-container">
      <div className="header">
        <h1>Reel Technical Operations</h1>
      </div>
      <div className="timeslot-buttons">
        {Array.from({ length: 15 }, (_, i) => (
          <button
            key={i}
            onClick={() => markAttendance(`Employee ${i + 1}`)}
            className="employee-btn"
          >
            Employee {i + 1}
          </button>
        ))}
      </div>
      <div className="action-buttons">
        <button onClick={onBackClick} className="back-btn">
          Back to Homepage
        </button>
        <a href="http://localhost:5001/api/download" className="download-btn">
          Download Sheet
        </a>
      </div>
    </div>
  );
};

export default Timeslot;
